-- Records of palylists
-- ----------------------------

-- ----------------------------
-- Table structure for song
-- ----------------------------
DROP TABLE IF EXISTS `song`;
CREATE TABLE `song`  (
  `songid` int(20) NOT NULL,
  `songname` varchar(255) CHARACTER SET utf8mb4 COLLATE utf8mb4_unicode_ci NULL DEFAULT NULL,
  `artistid` int(11) NULL DEFAULT NULL,
  `albumid` int(11) NULL DEFAULT NULL,
  `songimg` varchar(255) CHARACTER SET utf8mb4 COLLATE utf8mb4_unicode_ci NULL DEFAULT NULL,
  `status` int(1) NULL DEFAULT 0,
  `hits` bigint(20) NULL DEFAULT 0 COMMENT '点击量',
  PRIMARY KEY (`songid`) USING BTREE
) ENGINE = MyISAM CHARACTER SET = utf8mb4 COLLATE = utf8mb4_unicode_ci ROW_FORMAT = DYNAMIC;

